    ///
    /// Available since API-level: 20
    #[cfg(feature = "api-20")]
    #[cfg_attr(docsrs, doc(cfg(feature = "api-20")))]
    pub fn OH_AudioRenderer_GetLoudnessGain(
        renderer: *mut OH_AudioRenderer,
        loudnessGain: *mut f32,
    ) -> OH_AudioStream_Result;
